import React from "react";
import './Hooks.css';

function GetTime(){
    let currTime= new Date();
    let reload=()=>{
        document.location
    }

    return(
        <>
            <div className="content">
                <h1>{currTime.toLocaleTimeString()}</h1>
                <button onClick={reload}>Get Time</button>
            </div>
        </>
    );
}

export default GetTime;
