import React from "react";
function Calculator() {
    // let a=prompt("Enter A:");
    // let b=prompt("Enter B:");
    // let sum = (a, b) => {
    //     return a + b;
    // }
    function sum(a,b){
        let sum = a+b;
        return sum;
    }
    let sub = (a, b) => {
        return b - a;
    }
    let mul = (a, b) => {
        return a * b;
    }
    let div = (a, b) => {
        return a / b;
    }
    return (
        <>
            <h1 style={{textAlign:"center",color:"black"}}>Simple Calculator</h1>
            <ol>
                <li>{(`The Addition is:${sum(10,40)}`)}</li>
                <li>{console.log(`The substraction is:${sub()}`)}</li>
                <li>{console.log(`The Multiplication is:${mul()}`)}</li>
                <li>{console.log(`The Division is:${div()}`)}</li>
            </ol>
        </>
    )
}
export default Calculator;
